pragma solidity >=0.4.22;

contract Contract {
    struct Message_info {
        string senderID;
        string receiverID;
        string message;
    }

    mapping(uint256 => Message_info) public transactions;

    constructor(
        string memory message,
        string memory senderID,
        string memory receiverID,
        uint256 ids
    ) public {
        Message_info memory received = Message_info({
            senderID: senderID,
            receiverID: receiverID,
            message: message
        });
        transactions[ids] = received;
    }
}
