pragma solidity >=0.4.22;

import "./TransactionContract.sol";

contract Parent {
    uint256 counter = 0;

    function performTransaction(
        string memory message,
        string memory senderId,
        string memory receiverId
    ) public returns (Contract) {
        ++counter;
        Contract contr = new Contract(message, senderId, receiverId, counter);
        return contr;
    }
}
